import React from 'react';

function TodoItem({ text, completed, onToggle }) {
  return (
    <li onClick={onToggle} style={{ textDecoration: completed ? 'line-through' : 'none', cursor: 'pointer' }}>
      {text}
    </li>
  );
}

export default TodoItem;

import React, { useState } from 'react';
import TodoInput from './components/TodoInput';
import TodoList from './components/TodoList';

function App() {
  const [todos, setTodos] = useState([]);

  return (
    <div>
      <h1>My To-Do List</h1>
      <TodoInput setTodos={setTodos} />
      <TodoList todos={todos} setTodos={setTodos} />
    </div>
  );
}

export default App;

import React, { useState } from 'react';

function TodoInput({ setTodos }) {
  const [input, setInput] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (input.trim()) {
      setTodos(prevTodos => [...prevTodos, { text: input, completed: false }]);
      setInput('');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input value={input} onChange={(e) => setInput(e.target.value)} />
      <button type="submit">Add</button>
    </form>
  );
}

export default TodoInput;

import React from 'react';
import TodoItem from './TodoItem';

function TodoList({ todos, setTodos }) {
  const toggleTodo = (index) => {
    setTodos(prevTodos => {
      const newTodos = [...prevTodos];
      newTodos[index].completed = !newTodos[index].completed;
      return newTodos;
    });
  };

  return (
    <ul>
      {todos.map((todo, index) => (
        <TodoItem key={index} text={todo.text} completed={todo.completed} onToggle={() => toggleTodo(index)} />
      ))}
    </ul>
  );
}

export default TodoList;

